import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Mcard from '../Mcard/Mcard';
import './cards.css';

export default function Cards({ movies }) {
  const [originalMovies, setOriginalMovies] = useState([]);

  useEffect(() => {
    if (!movies || movies.length === 0) { // Only fetch popular movies if no search results
      axios
        .get("https://api.themoviedb.org/3/movie/popular", {
          params: {
            api_key: 'd05c4cd1b7f5c2480d55c1a2931702d5',
            language: 'en-US',
            page: 1,
          },
        })
        .then((res) => {
          setOriginalMovies(res.data.results);
        })
        .catch((err) => {
          console.error("Error fetching the movies:", err);
        });
    }
  }, [movies]);

  const displayedMovies = movies && movies.length > 0 ? movies : originalMovies;

  return (
    <div className="cards-container">
      {displayedMovies.map(movie => (
        <Mcard key={movie.id} movie={movie} />
      ))}
    </div>
  );
}
