import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function SingleMovie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  useEffect(() => {
    axios.get(`https://api.themoviedb.org/3/movie/${id}`, {
      params: {
        api_key: 'd05c4cd1b7f5c2480d55c1a2931702d5',
        language: 'en-US',
      },
    })
    .then(res => setMovie(res.data))
    .catch(err => console.error('Error fetching movie details:', err));
  }, [id]);

  if (!movie) return <div>Loading...</div>;

  return (
    <div>
      <div style={{ width: 1403, height: 1339, position: 'relative', background: 'white' }}>
        <div style={{ width: 838, left: 497, top: 308, position: 'absolute', color: 'black', fontSize: 24, fontFamily: 'Inter', fontWeight: '400', wordWrap: 'break-word' }}>{movie.overview}</div>
        <img style={{ width: 412, height: 618, left: 53, top: 95, position: 'absolute', borderRadius: 27 }} src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt="Poster" />
      </div>
    </div>
  );
}
