import React, { useState } from 'react';
import axios from 'axios';
import './welcom.css';

export default function Welcome({ setMovies }) {
  const [query, setQuery] = useState('');

  const handleSearch = () => {
    if (query.trim() === '') return;

    axios
      .get('https://api.themoviedb.org/3/search/movie', {
        params: {
          api_key: 'd05c4cd1b7f5c2480d55c1a2931702d5', // Your API key
          language: 'en-US',
          query: query,
          page: 1,
        },
      })
      .then((res) => {
        setMovies(res.data.results);
      })
      .catch((err) => {
        console.error('Error searching for movies:', err);
      });
  };

  return (
    <div className="mx-5 px-5 py-5 mt-4" style={{ backgroundColor: '#F3F1F1' }}>
      <p style={{ fontSize: '32px', fontWeight: 600 }}>Welcome to our movie app</p>
      <p style={{ fontSize: '14px', fontWeight: 400 }}>
        Millions of movies, TV shows, and people to discover. Explore now.
      </p>
      <div className="d-flex">
        <input
          type="text"
          className="form-control"
          placeholder="Search and explore..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()} // Allows pressing "Enter" to trigger search
        />
        <button className="btn ms-3" style={{ backgroundColor: '#FFE353', fontSize: '14px', fontWeight: 400 }} onClick={handleSearch}>
          Search
        </button>
      </div>
    </div>
  );
}
