import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Welcome from './components/Welcome/welcome'; // Adjust the path as needed
import Cards from './components/Cards/cards'; // Adjust the path as needed
import './App.css';

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    axios
      .get("https://api.themoviedb.org/3/movie/popular", {
        params: {
          api_key: 'd05c4cd1b7f5c2480d55c1a2931702d5',
          language: 'en-US',
          page: 1,
        },
      })
      .then((res) => {
        setMovies(res.data.results);
      })
      .catch((err) => {
        console.error("Error fetching the movies:", err);
      });
  }, []);

  return (
    <div className="App">
      <Welcome setMovies={setMovies} />
      <Cards movies={movies} />
    </div>
  );
}

export default App;
