import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function SingleMovie() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    axios.get(`https://api.themoviedb.org/3/movie/${id}`, {
      params: {
        api_key: 'd05c4cd1b7f5c2480d55c1a2931702d5',
        language: 'en-US',
      },
    })
    .then(res => setMovie(res.data))
    .catch(err => console.error('Error fetching movie details:', err));
  }, [id]);

  if (!movie) return <div>Loading...</div>;

  return (
        <div class="container">
            <div class="row">
                <div class="col-md-12" style="background-color: white; height: 1339px; position: relative;">
                <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt="Poster" class="img-fluid rounded" style="width: 412px; height: 618px; position: absolute; top: 95px; left: 53px; border-radius: 27px;" />
                <div class="absolute" style="left: 497px; top: 308px; font-size: 24px; font-family: Inter; font-weight: 400; word-wrap: break-word;">
                    {movie.overview}
                </div>
                </div>
            </div>
        </div>  
);
}
