import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Card, Row, Col } from 'react-bootstrap';

const DscpCards = () => {
  const movies = [
    {
      id: 1,
      title: 'Black Widow',
      date: 'Sep 25, 2017',
      rating: 4,
      reviewCount: 9288,
      description: 'Natasha Romanoff, also known as Black Widow, confronts the darker parts of her ledger when a dangerous conspiracy with ties to her past arises.',
      poster: 'path_to_image' // Replace with your image URL or path
    },
    {
      id: 2,
      title: 'Black Widow',
      date: 'Sep 25, 2017',
      rating: 4,
      reviewCount: 9288,
      description: 'Natasha Romanoff, also known as Black Widow, confronts the darker parts of her ledger when a dangerous conspiracy with ties to her past arises.',
      poster: 'path_to_image' // Replace with your image URL or path
    },
    // Add more movies as needed
  ];

  return (
    <Row xs={1} md={2} className="g-4">
      {movies.map((movie) => (
        <Col key={movie.id}>
          <Card className="h-100">
            <Card.Img variant="top" src={movie.poster} />
            <Card.Body>
              <Card.Title>{movie.title}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">{movie.date}</Card.Subtitle>
              <div className="d-flex align-items-center mb-2">
                {Array.from({ length: 5 }).map((_, index) => (
                  <i
                    key={index}
                    className={`bi bi-star${index < movie.rating ? '-fill' : ''} text-warning me-1`}
                  ></i>
                ))}
                <span className="ms-2">{movie.reviewCount}</span>
              </div>
              <Card.Text>
                {movie.description.length > 80 ? `${movie.description.substring(0, 77)}...` : movie.description}
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </Row>
  );
};

export default DscpCards;
