import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Mcard from '../Mcard/Mcard';
import './cards.css';

export default function Cards({ query }) {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const endpoint = query
      ? 'https://api.themoviedb.org/3/search/movie'
      : 'https://api.themoviedb.org/3/movie/popular';

    const params = {
      api_key: 'd05c4cd1b7f5c2480d55c1a2931702d5',
      language: 'en-US',
      page: 1,
    };

    if (query) {
      params.query = query;
    }

    axios
      .get(endpoint, { params })
      .then((res) => {
        setMovies(res.data.results);
      })
      .catch((err) => {
        console.error('Error fetching the movies:', err);
      });
  }, [query]); // Re-run this effect when the query changes

  return (
    <div className="cards-container">
      {movies.map((movie) => (
        <Mcard key={movie.id} movie={movie} />
      ))}
    </div>
  );
}
