import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import './cards.css';

const Cards = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    axios.get("https://api.themoviedb.org/3/movie/popular", {
        params: {
          api_key: 'd05c4cd1b7f5c2480d55c1a2931702d5',
          language: 'en-US',
          page: 1,
        },
      })
      .then((res) => {
        setMovies(res.data.results);
      })
      .catch((err) => {
        console.error('Error fetching the movies:', err);
      });
  }, []);


  return (
    <div className="cards-container">
      {movies.map(movie => (
        <Card key={movie.id} style={{ width: '18rem', margin: '10px' }}>
          <Card.Img variant="top" src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
          <Card.Body>
            <Card.Title>{movie.title}</Card.Title>
            <Card.Text>
              {movie.overview}
            </Card.Text>
            <button class="btn btn-custom">
Custom Button
</button>

          </Card.Body>
        </Card>
      ))}
    </div>
  );
};

export default Cards;
