import React, { useState } from "react";
import axios from "axios";
import Cards from "../cards/cards"; // Import the Cards component
import "./welcom.css";

export default function Welcom() {
  const [searchTerm, setSearchTerm] = useState("");
  const [movies, setMovies] = useState([]); // Manage the state locally

  const handleSearch = () => {
    if (searchTerm.trim() === "") {
      setMovies([]); // Clear the movies if the search term is empty
      return;
    }

    axios
      .get("https://api.themoviedb.org/3/search/movie", {
        params: {
          api_key: "d05c4cd1b7f5c2480d55c1a2931702d5", // Replace with your API key
          query: searchTerm,
          language: "en-US",
          page: 1,
        },
      })
      .then((res) => {
        setMovies(res.data.results); // Store results in local state
      })
      .catch((err) => {
        console.error("Error fetching the movies:", err);
      });
  };

  return (
    <div className="mx-5 px-5 py-5 mt-4" style={{ backgroundColor: "#F3F1F1" }}>
      <p style={{ fontSize: "32px", fontWeight: 600 }}>
        Welcome to our movie app
      </p>
      <p style={{ fontSize: "14px", fontWeight: 400 }}>
        Millions of movies, TV shows and people to discover. Explore now.
      </p>
      <div className="d-flex">
        <input
          type="text"
          className="form-control"
          placeholder="Search and explore...."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()} // Trigger search on Enter key press
        />
        <button
          className="btn ms-3"
          style={{ backgroundColor: "#FFE353", fontSize: "14px", fontWeight: 400 }}
          onClick={handleSearch} // Trigger search on button click
        >
          Search
        </button>
      </div>

      {/* Conditionally render Cards component based on search results */}
      {movies.length > 0 && (
        <div className="mt-4">
          <Cards movies={movies} />
        </div>
      )}
    </div>
  );
}
